<?php include("session.php");if (isset($_POST['niveau'])) {    $query = 'INSERT INTO niveau (libelle) VALUES (?)';    $stmt = $bdd->prepare($query);    $stmt->bind_param("s", $_POST['libelle']);    if (!$stmt->execute()) {        printf("Erreur : %s\n", $stmt->error);        $alertFail = "Le niveau n'a pas pu être ajouté ou modifié.";    } else {        $alertSuccess = "Le niveau a bien été ajouté ou modifié.";    }}if (isset($_POST['del-niveau'])) {    if (!$bdd->query('DELETE FROM niveau WHERE idNiveau = ' . $_POST['del-niveau'])) {        $alertDelFail = "Le niveau n'a pas pu être supprimé.";    } else {        $alertDelSuccess = "Le niveau a bien été supprimé.";    }}?><!doctype html><html lang="fr"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link href="../css/bootstrap.css" rel="stylesheet">    <title>Niveaux</title></head><body><?php include("navbar.php"); ?><div class="container">    <div class="row">        <div class="col-xl-auto mx-auto text-center"> <!--Formulaire de création d'un niveau-->            <h1 class="mb-4">Ajout d'un Niveau</h1>            <form class="mb-3" action="" method="post" name="niveau">                <div class="col-sm mb-3">                    <div class="form-floating">                        <input class="form-control" name="libelle" type="text"                               placeholder="Nom" maxlength="50" required>                        <label for="libelle">Niveau</label>                    </div>                </div>                <input class="btn btn-primary" type="submit" value="<?php echo $button ?>" name="niveau">            </form>            <?php if (!empty($alertSuccess)) {                echo '<div class="mt-3 alert alert-success text-center">'                    . $alertSuccess .                    '</div>';            } elseif (!empty($alertFail)) {                echo '<div class="mt-3 alert alert-danger text-center">'                    . $alertFail .                    '</div>';            }            ?>        </div>        <div class="col-xl-auto mx-auto">            <h1 class="text-center mb-4">Liste des Niveaux</h1>            <?php if (!empty($alertDelFail)) {                echo '<div class="mt-3 alert alert-danger text-center">'                    . $alertDelFail .                    '</div>';            } elseif (!empty($alertDelSuccess)) {                echo '<div class="mt-3 alert alert-success text-center">'                    . $alertDelSuccess .                    '</div>';            } ?>            <!--Tableau de la liste des Niveaux-->            <table class="table table-striped border border-3 text-center">                <thead>                <tr>                    <th scope="col">Niveau</th>                    <th scope="col">Action</th>                </tr>                </thead>                <tbody>                <?php                $query = $bdd->query('SELECT * FROM niveau ORDER BY idNiveau');                while ($resultat = $query->fetch_object()) { ?>                <tr>                    <td><?php echo $resultat->libelle ?></td>                    <td id="a">                        <a href="#"                           onclick="document.getElementById('del-niveau-<?php echo $resultat->idNiveau; ?>').submit()">Supprimer</a>                    </td>                    <form action="" method="post" id="del-niveau-<?php echo $resultat->idNiveau ?>">                        <input type="hidden" value="<?php echo $resultat->idNiveau ?>" name="del-niveau">                    </form>                    <?php }                    $query->close(); ?>                </tbody>            </table>        </div>    </div></div></body></html>